import React, {useState, useEffect} from "react";
import { useHistory } from "react-router-dom";
import { connect } from 'react-redux'
import Pagination from "../../../../libs/pagination/index";
import {getTeacherList, saveTeacher, deleteTeacher} from "../actions";
import RowData from "./components/courseRow"; 
import "../../../../css/adminpages.css";
import Loading from "../../../common/loading";

const TeacherList = (props) => {
	  const [page, setPage] = useState(0);
	  const [items, setItems] = useState(10);
	  const [count, setCount] = useState(0);
	  const [loading, setLoading] = useState(false);
	  const history = useHistory();
  
      useEffect(() => {
        getData();
    }, [page, items]);
  
  
      const getData = () => {
        setLoading(true);
        props.getTeacherList(page, items).then(res => {
            setLoading(false)
            setCount(res.result.data.count || 0);
        }).catch(err => setLoading(false));
    }
	
	
	    const handlePagination = (page) => {
        setPage(page);
    }
  
  
      // const handleNew = (obj = null) => { 
        // console.log("obj ==== ", obj);
        // props.storeCourse(obj);
        // let path = "/admin/teacher/";
        // path += obj && obj._id ? obj._id : "new";
        // history.push("/admin/teacher/new");
    // }
	
	    const handleCount = (e) => {
        setItems(e.target.value);
    }
  
      const handleTeacherDelete = (id) => {
        deleteTeacher(id).then(res => {
            console.log("res === ", res);
            getData();
        }).catch(err => {
            console.log("err === ", err);
        })
    }
	


  const handleNew = () => {
    history.push("/admin/teacher/new");
  };
  


  return (
    <div className="dash-data">
      <div className="courses-strip">
        <div className="flexible">
          <div className="course-side">
            <img src="/images/icons/teachers-icon.png" /> Teachers
          </div>
          <div className="addnew">
            <button onClick={handleNew}>
              <i className="fa fa-plus"></i> Add new teacher
            </button>
          </div>
        </div>
      </div>
      <div className="teachers">
        <span className="count-holder">Show <input type="number" className="count" value={items} onChange={handleCount}/></span>
            <span><input id="myInput" className="table-search" type="text" placeholder="Search.." />
            {loading && <Loading />}
            {props.list && props.list.length > 0 ?
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Gender</th>
              <th>Age</th>
              <th>Registerd By</th>
			  <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody id="myTable">
            {props.list.map((record, index) => <RowData key={record._id} id={page > 0 ? page*items + index + 1 : index +1} record={record} editcallback={handleNew} deleteRecord={handleTeacherDelete}/>) 
                    // <tr key={record._id}>
                    //         <td>{page > 0 ? page*items + index + 1 : index +1}</td>
                    //         <td>{record.title}</td>
                    //         <td>{record.category[0] || ''}</td>
                    //         <td>{record.subCategory[0] || ''}</td>
                    //         <td>{'-'}</td>
                    //         <td>{'-'}</td>
                    //         <td><i className="fa fa-inr"></i> {record.pricing && record.pricing.price ? record.pricing.price : '-'}</td>
                    //         <td>{record.isActive ? <button className="activebtn-dash">active</button> : <button className="activebtn-dash">Inactive</button>}</td>
                    //         <td onClick={() => setToggle(!menu)}><i className="fa fa-ellipsis-v"></i>
                    //             {menu && <div className="dropdown-menu">
                    //                 <button className="dropdown-item" onClick={() =>  handleCourse(record)}>Test</button>
                    //                 <a className="dropdown-item" href="#">View</a>
                    //                 <a className="dropdown-item" onClick={() => handleCourse(record)}>Edit</a>
                    //                 <a className="dropdown-item" onClick={() => handleCourseDelete(record)}>Delete</a>
                    //             </div>}
                    //         </td>
    
                    //     </tr>)
            }
                </tbody>
            </table>
            : <p> No Data</p>}
            </span>
      </div>
      <Pagination
        count={100}
        page={page}
        items={items}
        callback={handlePagination}
      />
    </div>
  );
};


const mapStateToProps = (state) => {
    return { list: state.admincourse.teacherList };
}

const mapDispatchToProps = (dispatch) => {
    return {
        getTeacherList: (page, limit) => (dispatch(getTeacherList(page, limit))),
        storeTeacher: (obj) => (dispatch(saveTeacher(obj)))
    };
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(TeacherList);
