import React, { useRef, useState } from "react";
import Button from "react-bootstrap/Button";
import { ForgotPasswordAction } from "../actions";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import "./signup.css";
import "../../../../css/modal.css";

const ForgotPassword = (props) => {
  const [mobile, setmobile] = useState("");
  const email = useRef(null);
  const handleSubmit = () => {
    let val = email.current.value;
    ForgotPasswordAction(val).then((res) => props.showLogin(""));
  };

  return (
    <div className="content-part default">
      <h3>Forgot Your Password?</h3>
      {/*} <h3 className="mb-28">Welcome Back</h3>*/}
      <p className="mb-28">
        please confirm your email/mobile below and we will send you a
        verification code.
      </p>

      <div className="form-group">
        <PhoneInput country="IN" value={mobile} onChange={setmobile} />
        <label htmlFor="mobile" className="control-label">
          Enter Mobile number
        </label>
        <i className="bar"></i>
      </div>

      <div className="clearfix"></div>
      <div className="row">
        <div className="col-lg-3"></div>
        <div className="col-lg-6">
          {/* className="btn btn-primary btn-lg btn-block" */}
          <Button size="lg" onClick={handleSubmit} block>
            Reset Password
          </Button>
        </div>
        <div className="col-lg-3"></div>
        {/*<div className="col-lg-6">
          <button
            className="btn otpbtn btn-lg btn-block"
            onClick={() => props.showLogin("")}
          >
            Login
          </button>
  </div>*/}
      </div>
      <div id="inline-popups3">
        <p className="member signbtn">
          Already have an account ? &nbsp;{" "}
          <a
            onClick={props.showLogin}
            data-toggle="modal"
            data-target="#basicModal"
            data-dismiss="modal"
            className="show-cursor"
          >
            Sign in
          </a>
        </p>
      </div>
    </div>
  );
};

export default ForgotPassword;
