import React, { useState, useEffect } from "react";
import "../../../css/adminpages.css";
import { connect } from 'react-redux'
import PhoneInput, {
  isValidPhoneNumber,
  parsePhoneNumber,
} from "react-phone-number-input";
import {changePassword, updateProfile} from "./actions";

const Settings = (props) => {
  const [userData, setuser] = useState({ firstName: '', lastName: '', email: '', mobileNumber: '', gender: '' });
  const [pass, setPass] = useState({old: '', new: '', confirm: ''});

  useEffect(() => {
    if (props.user) {
      const { user } = props;
      setuser({
        firstName: user.firstName ? user.firstName : '',
        lastName: user.lastName ? user.lastName : '',
        email: user.email ? user.email : '',
        mobileNumber: user.mobileNumber ? user.mobileNumber : '',
        gender: user.gender ? user.gender : ''
      });
    }
  }, [props.user]);

  const handleChange = (name, value) => {
    setuser(prevState => {return {...prevState, [name]: value}});
  }

  const handlePassword = (name, value) => {
    setPass(prevState => {return {...prevState, [name]: value}});
  }

  const handleSave = () => {
    if (compareObj(userData, props.user)) {
      handleProfile();
    }
    if (pass.old !== '' || pass.new !== '' || pass.confirm !== '') {
      validatePassword();
    }
  }

  const compareObj = (obj1, obj2) => {
    let keys = Object.keys(obj1);
    for (let i in keys) {
      if ((obj2[keys[i]] === undefined && obj1[keys[i]] !== '') || (obj2[keys[i]] !== undefined && obj1[keys[i]] !== obj2[keys[i]])) {
        return true;
      }
    }
    return false;
  }

  const handleProfile = () => {
    let obj = {};
    if (userData.firstName !== '') {
      obj['firstName'] = userData.firstName;
    } else {
      alert("Please enter the First Name");
      return;
    }

    if (userData.lastName !== '') {
      obj['lastName'] = userData.lastName;
    } else {
      alert("Please enter the Last Name");
      return;
    }

    if (userData.gender !== '') {
      obj['gender'] = userData.gender;
    } else {
      alert("Please enter the Gender");
      return;
    }

    if (userData.mobileNumber !== '') {
      obj['mobileNumber'] = userData.mobileNumber;
    } else {
      alert("Please enter the Mobile Number");
      return;
    }

    if (userData.email !== '') {
      obj['email'] = userData.email;
    } else {
      alert("Please enter the Email");
      return;
    }
    props.updateProfile(obj, props.user._id).then(res => console.log(res)).catch(err => {
      console.log("err === ", err);
    })
    
  }

  const validatePassword = () => {
    let obj = {};
    if (pass.old !== '') {
      obj['currentPassword'] = pass.old;
    } else {
      alert("Please enter the Old Password");
      return;
    }

    if (pass.new === '') {
      alert("Please enter the New Password");
      return;
    }

    if (pass.new === pass.confirm) {
      obj['newPassword'] = pass.confirm;
    } else {
      alert("Both new and confirm password should be same");
      return;
    }

    changePassword(obj).then(res => {
      // console.log("res === ", res);
      setPass({old: '', new: '', confirm: ''})
      alert(res);
    }).catch(err => {
      console.log("err === ", err)
      alert(err.message);
    });
  }

  return (
    <div className="dash-data">
      <div className="courses-strip">
        <div className="flexible">
          <div className="course-side">
            {/*<img src="/images/icons/setting-icon.png" />*/}
            <img src="/images/icons/teachers-icon.png" />
            Setting
          </div>
        </div>
      </div>
      <div className="setting">
        <p className="profile_p">
          Profile{" "}
          <i
            style={{ color: "#989898", fontSize: "17px" }}
            className="fa fa-pencil"
          ></i>
        </p>
        <form>
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={userData.firstName} onChange={(e) => handleChange('firstName', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  First Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={userData.lastName} onChange={(e) => handleChange('lastName', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  Last Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={userData.gender} onChange={(e) => handleChange('gender', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  Gender
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-6">
              <div className="form-group">
                <PhoneInput
                  // placeholder="Enter phone number"
                  country="IN"
                  value={userData.mobileNumber}
                  onChange={(num) => handleChange('mobileNumber', num)}
                />
                {/* <input type="text" required="required" value={user.phoneNumber} onChange={(e) => handleChange('phoneNumber', e.target.value)} /> */}
                <label htmlFor="input" className="control-label">
                  Mobile
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-6">
              <div className="form-group">
                <input type="text" required="required" value={userData.email} onChange={(e) => handleChange('email', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  Email Id
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <br />
          <p className="profile_p">
            Change password{" "}
            <i
              style={{ color: "#989898", fontSize: "17px" }}
              className="fa fa-pencil"
            ></i>
          </p>
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={pass.old} onChange={(e) => handlePassword('old', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  Old password
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={pass.new} onChange={(e) => handlePassword('new', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  New password
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" value={pass.confirm} onChange={(e) => handlePassword('confirm', e.target.value)} />
                <label htmlFor="input" className="control-label">
                  Confirm password
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <br />
          <p className="center">
            <button className="btnsave" onClick={handleSave}>Save</button>
          </p>
        </form>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return { user: state.app.userData };
}

const mapDispatchToProps = (dispatch) => {
  return {
    updateProfile: (obj, id) => (dispatch(updateProfile(obj, id)))
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Settings);
