import React, {useState, useEffect} from "react";

import Loading from "../../../../common/loading";

export default (props) => {
	
	const [loading, setLoading] = useState(true);
	
	    const handleSave = () => {
        const {course} = props;
        setLoading(true);
        let obj = {};
        if (name && name !== "") {
            obj['title'] = name;
        } else {
            alert("Please enter Name");
            setLoading(false);
            return;
        }
        if (desc && desc !== "") {
            obj['description'] = desc;
        } else {
            alert("Please enter Course description");
            setLoading(false);
            return;
        }
        if (benefits && benefits !== "") {
            obj['benefits'] = benefits;
        } else {
            alert("Please enter Benefits");
            setLoading(false);
            return;
        }

        if (targetUser && targetUser !== "") {
            obj['targetUser'] = targetUser;
        }
        for (let i in data) {
            if (data[i] !== "") obj[i] = [data[i]];
        }

        props.saveCallback(obj);
    }
	
  return (
    <div className="tabdb active">
      <div className="setting">
        <form>
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  First Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Last Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Gender
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Dob
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Mobile
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Email
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" required="required" />
                <label for="input" className="control-label">
                  Designation
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4"></div>
            <div className="col-lg-4"></div>
          </div>
          <p>
            Role <a href="#">Edit</a>
          </p>
          <br />
          <br />
          <hr />
          <div className="row">
                <div className="col-lg-6 col-md-6">
                    <button className="savebtn"  onClick={handleSave}>Save</button>
                </div>
                <div className="col-lg-6 col-md-6 text-right">
                    <button className="nxtbtn" onClick={handleSave}>Next</button>
                </div>
				</div>
        </form>
      </div>
    </div>
  );
};
