import React from "react";
import { useLocation, useHistory, Link } from "react-router-dom";
import "../../../../css/sidebar.css";

const AdminSideNav = (props) => {
  let location = useLocation();
  const history = useHistory();

  console.log(location.pathname);

  const logout = () => {
    localStorage.clear();
    history.push("/");
  };

  return (
    <div className="dash-sidebar">
      <ul>
        <li>
          <Link
            className={
              /admin\/dashboard/g.test(location.pathname) ? "active" : ""
            }
            to="/admin/dashboard"
          >
            <i className="fa fa-home"></i> Dashboard
          </Link>
        </li>
        {/*<li>
          <a
            className={
              /admin\/category/g.test(location.pathname) ? "active" : ""
            }
            href="/admin/category"
          >
            <i className="fa fa-list-alt"></i> Categories
          </a>
          </li>*/}
        <li>
          <Link
            className={
              /admin\/faculty/g.test(location.pathname) ? "active" : ""
            }
            to="/admin/faculty"
          >
            <i className="fa fa-university"></i> Faculty
          </Link>
        </li>
        <li>
          <Link
            className={/admin\/course/g.test(location.pathname) ? "active" : ""}
            to="/admin/course"
          >
            <i className="fa fa-youtube-play"></i> Courses
          </Link>
        </li>
        <li>
          <Link
            className={/admin\/teacher/g.test(location.pathname) ? "active" : ""}
            to="/admin/teacher"
          >
            <i className="fa fa-users"></i> Teachers
          </Link>
        </li>
        <li className="enrolled" data-toggle="collapse" data-target="#demo">
          <Link
            className={
              /admin\/enrolment/g.test(location.pathname) ? "active" : ""
            }
            to="#"
          >
            <i className="fa fa-sticky-note-o"></i> Enrolment{" "}
            <i
              className="fa fa-angle-down pull-right"
              style={{ fontSize: "20px" }}
            ></i>
          </Link>
          <ul id="demo" className="collapse">
            <li>
              <Link
                className={location.pathname === "/admin/enrolment/history" ? "active" : ""}
                to="/admin/enrolment/history"
              >
                Enrol History
              </Link>
            </li>
            <li>
              <Link
                className={location.pathname === "/admin/enrolment/teacher" ? "active" : ""}
                to="/admin/enrolment/teacher"
              >
                Enrol a Teacher
              </Link>
            </li>
          </ul>
        </li>
        <li>
          <Link
            className={
              /admin\/setting/g.test(location.pathname) ? "active" : ""
            }
            to="/admin/setting"
          >
            <i className="fa fa-cog"></i> Setting
          </Link>
        </li>
        <li>
          <Link onClick={logout}>
            <i className="fa fa-power-off"></i> Sign out
          </Link>
        </li>
      </ul>
    </div>
  );
};

export default AdminSideNav;
