import React, {useState, useEffect} from "react";
import Dropdown from "react-bootstrap/Dropdown";
import Loading from "../../../../common/loading";

export default (props) => {
	
	const [loading, setLoading] = useState(true);
	const [firstname, setTarUser] = useState('');
    const [data, setData] = useState({firstname: '', lastname: '',  gender: '', dob: '', dummyLayerTwo: '',  gender:  '', })
										
	 useEffect(() => {
        const {teacher} = props;
        console.log("teacher === ", teacher);
        if (teacher) {
            teacher.firstname && setTarUser(teacher.firstname);
            let keys = Object.keys(data);
            let obj = {};
            for(let i in keys) {
                obj[keys[i]] = teacher[keys[i]] && teacher[keys[i]][0] ? teacher[keys[i]][0] : '';
            }
            console.log("obj === ", obj);
            setData(prevState => {return obj});
            setLoading(false);
        } else {
            setLoading(false); 
        }
    }, [props.teacher]);
	
	    const handleChange = (name, value) => {
        setData(prevState => {return {...prevState, [name]: value }});
    };
	
	    const handleSave = () => {
        const {teacher} = props;
        setLoading(true);
        let obj = {};

       

        if (firstname && firstname !== "") {
            obj['firstname'] = firstname;
        }
        for (let i in data) {
            if (data[i] !== "") obj[i] = [data[i]];
        }

        props.saveCallback(obj);
    }
	
  return (
    <div className="tabdb active">
      <div className="setting">
        <form>
          <div className="row">
             <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="firstname" onChange={(e) => handleChange("firstname", e.target.value)} />
                <label for="input" className="control-label">
                  Last Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="lastname" onChange={(e) => handleChange("lastname", e.target.value)} />
                <label for="input" className="control-label">
                  Last Name
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="gender" onChange={(e) => handleChange("gender", e.target.value)} />
                <label for="input" className="control-label">
                  Gender
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="dob" onChange={(e) => handleChange("dob", e.target.value)} />
                <label for="input" className="control-label">
                  Dob
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="mobile" onChange={(e) => handleChange("mobile", e.target.value)} />
                <label for="input" className="control-label">
                  Mobile
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="dummyLayerTwo" onChange={(e) => handleChange("dummyLayerTwo", e.target.value)} />
                <label for="input" className="control-label">
                  Email
                </label>
                <i className="bar"></i>
              </div>
            </div>
          </div>
          <br />
          <div className="row">
            <div className="col-lg-4">
              <div className="form-group">
                <input type="text" name="category" onChange={(e) => handleChange("category", e.target.value)} />
                <label for="input" className="control-label">
                  Designation
                </label>
                <i className="bar"></i>
              </div>
            </div>
            <div className="col-lg-4"></div>
            <div className="col-lg-4"></div>
          </div>
          <p>
            Role <a href="#">Edit</a>
          </p>
          <br />
          <br />
          <hr />
          <div className="row">
                <div className="col-lg-6 col-md-6">
                    <button className="savebtn"  onClick={handleSave}>Save</button>
                </div>
                <div className="col-lg-6 col-md-6 text-right">
                    <button className="nxtbtn" onClick={handleSave}>Next</button>
                </div>
				</div>
        </form>
      </div>
    </div>
  );
};
