import React, {useState, useEffect} from "react";
import Dropdown from "react-bootstrap/Dropdown";
import Loading from "../../../../common/loading";

export default (props) => {

    const [name, setName] = useState('');
    const [desc, setDesc] = useState('');
    const [benefits, setBenefits] = useState('');
	
    const [loading, setLoading] = useState(true);
    const [targetUser, setTarUser] = useState('');
    const [data, setData] = useState({targetSubUser: '', dummyLayerOne: '', 
                                        board: '', ageLevel: '', dummyLayerTwo: '', 
                                        category: '', subCategory: '', })
    useEffect(() => {
        const {course} = props;
        console.log("course === ", course);
        if (course) {
            course.title && setName(course.title);
            course.description && setDesc(course.description);
            course.benefits && setBenefits(course.benefits);
            course.targetUser && setTarUser(course.targetUser);
            let keys = Object.keys(data);
            let obj = {};
            for(let i in keys) {
                obj[keys[i]] = course[keys[i]] && course[keys[i]][0] ? course[keys[i]][0] : '';
            }
            console.log("obj === ", obj);
            setData(prevState => {return obj});
            setLoading(false);
        } else {
            setLoading(false); 
        }
    }, [props.course]);

    const handleChange = (name, value) => {
        setData(prevState => {return {...prevState, [name]: value }});
    };

    const handleSave = () => {
        const {course} = props;
        setLoading(true);
        let obj = {};
        if (name && name !== "") {
            obj['title'] = name;
        } else {
            alert("Please enter Name");
            setLoading(false);
            return;
        }
        if (desc && desc !== "") {
            obj['description'] = desc;
        } else {
            alert("Please enter Course description");
            setLoading(false);
            return;
        }
        if (benefits && benefits !== "") {
            obj['benefits'] = benefits;
        } else {
            alert("Please enter Benefits");
            setLoading(false);
            return;
        }

        if (targetUser && targetUser !== "") {
            obj['targetUser'] = targetUser;
        }
        for (let i in data) {
            if (data[i] !== "") obj[i] = [data[i]];
        }

        props.saveCallback(obj);
    }

    return <div className="tabdb active">
        {loading && <Loading />}
        <h5>Basic Detail</h5>
        <input type="text" className="course-search" placeholder="Type course name here" value={name} onChange={(e) => setName(e.target.value)} />
        <div className="form-select">
            <div className="row">
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Target user</p>
                    <select className="nice-select" name="targetUser" onChange={(e) => setTarUser(e.target.value)}>
                        <option value="" data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Teacher</option>
                        <option value="2" className="list option">Teacher 2</option>
                        <option value="4" className="list option">Teacher 3</option>
                    </select>
                </div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Target sub-user</p>
                    <select className="nice-select" name="targetSubUser" onChange={(e) => handleChange("targetSubUser", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Enter sub-category</option>
                        <option value="2" className="list option">Enter sub-category 2</option>
                        <option value="4" className="list option">Enter sub-category 3</option>
                    </select>
                </div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Dummy Layer</p>
                    <select className="nice-select" name="dummyLayerOne" onChange={(e) => handleChange("dummyLayerOne", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Layer</option>
                        <option value="2" className="list option">Layer 2</option>
                        <option value="4" className="list option">Layer 3</option>
                    </select>
                </div>
                <div className="clearfix"></div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Board</p>
                    <select className="nice-select" name="board" onChange={(e) => handleChange("board", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">CBSE</option>
                        <option value="2" className="list option">CBSE 2</option>
                        <option value="4" className="list option">CBSE 3</option>
                    </select>
                </div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Age Level</p>
                    <select className="nice-select" name="ageLevel" onChange={(e) => handleChange("ageLevel", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">14-15 years</option>
                        <option value="2" className="list option">16-18 years</option>
                        <option value="4" className="list option">18-20 years</option>
                    </select>
                </div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Dummy Layer</p>
                    <select className="nice-select" name="dummyLayerTwo" onChange={(e) => handleChange("dummyLayerTwo", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Layer</option>
                        <option value="2" className="list option">Layer 2</option>
                        <option value="4" className="list option">Layer 3</option>
                    </select>
                </div>
                <div className="clearfix"></div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Category <span className="pull-right"><a className="addcat" href="#">Add</a></span></p>
                    <select className="nice-select" name="category" onChange={(e) => handleChange("category", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Category</option>
                        <option value="2" className="list option">Category 2</option>
                        <option value="4" className="list option">Category 3</option>
                    </select>
                </div>
                <div className="col-lg-4 mb-20">
                    <p className="user-label">Sub-category <span className="pull-right"><a className="addcat" href="#">Add</a></span></p>
                    <select className="nice-select" name="subCategory" onChange={(e) => handleChange("subCategory", e.target.value)}>
                        <option data-display="Select" className="list option selected focus">Nothing</option>
                        <option value="1" className="list option">Sub-category</option>
                        <option value="2" className="list option">Sub-category 2</option>
                        <option value="4" className="list option">Sub-category 3</option>
                    </select>
                </div>
            </div>
            <p className="user-label">Course Description</p>
            <textarea className="form-control" placeholder="Course Introduction" value={desc}  onChange={(e) => setDesc(e.target.value)} ></textarea>
            <br />
            <p className="user-label">Course Benefits</p>
            <textarea className="form-control" placeholder="Benefits"  value={benefits} onChange={(e) => setBenefits(e.target.value)} ></textarea>
            <hr />
            <div className="row">
                <div className="col-lg-6 col-md-6">
                    <button className="savebtn"  onClick={handleSave}>Save</button>
                </div>
                <div className="col-lg-6 col-md-6 text-right">
                    <button className="nxtbtn" onClick={handleSave}>Next</button>
                </div>
            </div>
        </div>
    </div>

}
